mapper.select:
select * from artist



            and name like CONCAT(CONCAT('%',#{artist.name}),'%')


            and country=#{artist.country}


            and sex=#{artist.sex}


            and area=#{artist.area}


            and artistType=#{artist.artistType}


where s.id in
(
  select songId from song_with_artist where artistId=#{artistId}
)
and s.type=#{type}
